mapper.update:
UPDATE dict_types
type_code = #{typeCode}, type_name = #{typeName}, description = #{description}, sort_order = #{sortOrder}, is_active = #{isActive}, updated_at = #{updatedAt}, deleted_at = #{deletedAt}
WHERE id = #{id} AND deleted_at IS NULL

mapper.insert:
INSERT INTO dict_types (
    type_code, type_name, description, sort_order, is_active, created_at, updated_at
) VALUES (
    #{typeCode}, #{typeName}, #{description}, #{sortOrder}, #{isActive}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT COUNT(1)
FROM dict_data
WHERE type_id = #{typeId}
AND data_value = #{dataValue}

    AND id != #{excludeId}


    AND deleted_at IS NULL

mapper.update:
UPDATE dict_data
type_id = #{typeId}, data_label = #{dataLabel}, data_value = #{dataValue}, description = #{description}, sort_order = #{sortOrder}, is_active = #{isActive}, updated_at = #{updatedAt}
WHERE id = #{id} AND deleted_at IS NULL
