mapper.select:
SELECT *
FROM dict_types
WHERE deleted_at IS NULL

    AND (type_name LIKE CONCAT('%', #{keyword}, '%')
    OR type_code LIKE CONCAT('%', #{keyword}, '%')
    OR description LIKE CONCAT('%', #{keyword}, '%'))


    AND type_code = #{typeCode}


    AND type_name LIKE CONCAT('%', #{typeName}, '%')


    AND is_active = #{isActive}

ORDER BY



            created_at ${sort.contains('desc') ? 'DESC' : 'ASC'}


            type_name ${sort.contains('desc') ? 'DESC' : 'ASC'}


            type_code ${sort.contains('desc') ? 'DESC' : 'ASC'}


            sort_order ASC, created_at DESC



        sort_order ASC, created_at DESC

mapper.update:
UPDATE dict_types
type_code = #{typeCode}, type_name = #{typeName}, description = #{description}, sort_order = #{sortOrder}, is_active = #{isActive}, updated_at = #{updatedAt}, deleted_at = #{deletedAt}
WHERE id = #{id} AND deleted_at IS NULL

mapper.select:
SELECT COUNT(1)
FROM dict_data
WHERE type_id = #{typeId}
AND data_value = #{dataValue}

    AND id != #{excludeId}


    AND deleted_at IS NULL

SELECT *
FROM dict_data
WHERE type_id = #{typeId}
  AND data_value = #{dataValue}
  AND deleted_at IS NULL

mapper.update:
UPDATE dict_data
type_id = #{typeId}, data_label = #{dataLabel}, data_value = #{dataValue}, description = #{description}, sort_order = #{sortOrder}, is_active = #{isActive}, updated_at = #{updatedAt}
WHERE id = #{id}
