mapper.select:
SELECT DATE_FORMAT(rainfall_monitoring_data.monitoring_time,
'%Y-%m-%d %H:%i:%s' '%Y-%m-%d' '%Y-%m' '%Y-%m-%d %H:%i:%s'
) as time_label, AVG(rainfall_monitoring_data.rainfall) as avg_rainfall, MAX(rainfall_monitoring_data.rainfall) as max_rainfall, MIN(rainfall_monitoring_data.rainfall) as min_rainfall, SUM(rainfall_monitoring_data.rainfall) as sum_rainfall, MAX(rainfall_monitoring_data.rainfall_intensity) as max_rainfall_intensity, AVG(rainfall_monitoring_data.rainfall_intensity) as avg_rainfall_intensity, MAX(rainfall_monitoring_data.cumulative_rainfall) as max_cumulative_rainfall, MIN(rainfall_monitoring_data.cumulative_rainfall) as min_cumulative_rainfall, COUNT(*) as data_count FROM rainfall_monitoring_data WHERE rainfall_monitoring_data.deleted_at IS NULL AND rainfall_monitoring_data.station_id = #{stationId} AND rainfall_monitoring_data.monitoring_time >= #{startTime} AND rainfall_monitoring_data.monitoring_time <= #{endTime} GROUP BY time_label ORDER BY time_label ASC

SELECT DATE_FORMAT(rainfall_monitoring_data.monitoring_time,
'%Y-%m-%d %H:%i:%s' '%Y-%m-%d' '%Y-%m' '%Y-%m-%d %H:%i:%s'
) as time_label, AVG(rainfall_monitoring_data.rainfall) as avg_rainfall, MAX(rainfall_monitoring_data.rainfall) as max_rainfall, MIN(rainfall_monitoring_data.rainfall) as min_rainfall, SUM(rainfall_monitoring_data.rainfall) as sum_rainfall, MAX(rainfall_monitoring_data.rainfall_intensity) as max_rainfall_intensity, AVG(rainfall_monitoring_data.rainfall_intensity) as avg_rainfall_intensity, MAX(rainfall_monitoring_data.cumulative_rainfall) as max_cumulative_rainfall, COUNT(*) as data_count, COUNT(DISTINCT rainfall_monitoring_data.station_id) as station_count FROM rainfall_monitoring_data LEFT JOIN monitoring_stations ON rainfall_monitoring_data.station_id = monitoring_stations.id AND monitoring_stations.deleted_at IS NULL WHERE rainfall_monitoring_data.deleted_at IS NULL AND monitoring_stations.monitoring_item_code = 'R' AND rainfall_monitoring_data.monitoring_time >= #{startTime} AND rainfall_monitoring_data.monitoring_time <= #{endTime} GROUP BY time_label ORDER BY time_label ASC

SELECT COUNT(*)
FROM rainfall_monitoring_data
WHERE deleted_at IS NULL
AND station_id = #{stationId}
AND monitoring_time = #{monitoringTime}
