mapper.select:
SELECT COUNT(1) FROM roles
WHERE name = #{name} AND deleted_at IS NULL

    AND id != #{excludeId}

mapper.update:
UPDATE roles


        name = #{name},


        description = #{description},


        sort_order = #{sortOrder},


        is_active = #{isActive},

    updated_at = #{updatedAt}

        , deleted_at = #{deletedAt}


WHERE id = #{id}

    AND deleted_at IS NULL

mapper.select:
SELECT COUNT(1)
FROM user_roles
WHERE role_id = #{roleId}
