mapper.select:
SELECT COUNT(*) > 0 FROM villages
WHERE name = #{name} AND administrative_code = #{administrativeCode}

    AND id != #{excludeId}

AND deleted_at IS NULL

mapper.update:
UPDATE villages
name = #{name}, longitude = #{longitude}, latitude = #{latitude}, administrative_code = #{administrativeCode}, current_population = #{currentPopulation}, remark = #{remark}, updated_at = #{updatedAt}, deleted_at = #{deletedAt}
WHERE id = #{id}
