mapper.select:
SELECT DATE_FORMAT(water_level_monitoring_data.monitoring_time,
'%Y-%m-%d %H:%i:%s' '%Y-%m-%d' '%Y-%m' '%Y-%m-%d %H:%i:%s'
) as time_label, AVG(water_level_monitoring_data.water_level) as avg_water_level, MAX(water_level_monitoring_data.water_level) as max_water_level, MIN(water_level_monitoring_data.water_level) as min_water_level, COUNT(*) as data_count FROM water_level_monitoring_data WHERE water_level_monitoring_data.deleted_at IS NULL AND water_level_monitoring_data.station_id = #{stationId} AND water_level_monitoring_data.monitoring_time >= #{startTime} AND water_level_monitoring_data.monitoring_time <= #{endTime} GROUP BY time_label ORDER BY time_label ASC

SELECT DATE_FORMAT(water_level_monitoring_data.monitoring_time,
'%Y-%m-%d %H:%i:%s' '%Y-%m-%d' '%Y-%m' '%Y-%m-%d %H:%i:%s'
) as time_label, AVG(water_level_monitoring_data.water_level) as avg_water_level, MAX(water_level_monitoring_data.water_level) as max_water_level, MIN(water_level_monitoring_data.water_level) as min_water_level, COUNT(*) as data_count, COUNT(DISTINCT water_level_monitoring_data.station_id) as station_count FROM water_level_monitoring_data LEFT JOIN monitoring_stations ON water_level_monitoring_data.station_id = monitoring_stations.id AND monitoring_stations.deleted_at IS NULL WHERE water_level_monitoring_data.deleted_at IS NULL AND monitoring_stations.monitoring_item_code = 'H' AND water_level_monitoring_data.monitoring_time >= #{startTime} AND water_level_monitoring_data.monitoring_time <= #{endTime} GROUP BY time_label ORDER BY time_label ASC

SELECT COUNT(*)
FROM water_level_monitoring_data
WHERE deleted_at IS NULL
AND station_id = #{stationId}
AND monitoring_time = #{monitoringTime}
